<?php	session_start();	include_once '../config.php';			$stud_id = $_SESSION['sid'];	$mid = $_GET['online_id'];	$qno = $_GET['qno'];	$response = $_GET['response'];		$sql = "INSERT INTO online_schedule_test_response 			(`stud_id`,`mat_online_test_id`,`que_no`,`response`)			SELECT '$stud_id','$mid','$qno','$response' 			FROM online_schedule_test_response			WHERE NOT EXISTS (SELECT * 			FROM online_schedule_test_response WHERE stud_id='$stud_id' 			AND mat_online_test_id='$mid' AND que_no='$qno') LIMIT 1";	echo $sql."<br/>";				$result = mysql_query($sql);	if ($result)	{		echo "success...";	}	else	{		echo "failed...";	}	include_once '../conn_close.php';?>