<?php	session_start();	include_once '../config.php';		$mid = $_GET['mat_id'];	$sid = $_GET['stud_id'];		echo "<table BORDER='3' CELLSPACING='1' CELLPADDING='1' style='width:97%' >";	echo "<tr>			<th style='width:20%;'>				Date			</th>			<th style='padding-left:15px;width:13%;'>				Correct				&nbsp;&nbsp;			</th>			<th style='padding-left:15px;width:13%;'>				Incorrect				&nbsp;&nbsp;			</th>			<th style='padding-left:15px;width:13%;'>				Unattempt				&nbsp;&nbsp;			</th>			<th style='padding-left:15px;width:13%;'>				Total				&nbsp;&nbsp;			</th>			<th style='padding-left:15px;width:18%;'>				Time Taken (HH:MM:SS)			</th>		</tr>";			$SQL = "SELECT DATE_FORMAT(DATE(start_time),'%d %b %Y') as date,			corr_que_total AS corr,incorr_que_total AS incorr,			unattempt_total AS unattempt,(corr_que_total+incorr_que_total+unattempt_total) AS total,			TIMEDIFF(TIME(end_time),TIME(start_time)) AS take_time,			TIME_FORMAT(start_time,'%I:%i-%p') as time			FROM omr_student_score_history			WHERE student_id = $sid AND material_id = $mid";	$result=mysql_query($SQL) or die($SQL."<br/><br/>".mysql_error());	while($row=mysql_fetch_row($result))	{		echo "<tr>";		echo "<td style='width:20%;'>";				echo $row[0]."&nbsp;&nbsp;".$row[6];						echo "</td>			<td align='center' style='width:13%;'>";				echo $row[1]."&nbsp;&nbsp;";						echo "</td>			<td align='center'  style='padding-left:15px;width:13%;'>";				echo $row[2]."&nbsp;&nbsp;";		echo "</td>			<td align='center'  style='padding-left:15px;width:13%;'>";				echo $row[3]."&nbsp;&nbsp;";		echo "</td>			<td align='center'  style='padding-left:15px;width:13%;'>";				echo $row[4]."&nbsp;&nbsp;";		echo "</td>			<td align='center'  style='padding-left:15px;width:18%;'>";				echo $row[5]."&nbsp;&nbsp;";		echo "</td>		</tr>";	}	echo "</table>";	include_once '../conn_close.php';?>