<?php	include '../config.php';	session_start();		$page = isset($_POST['page']) ? intval($_POST['page']) : 1;	$rows = isset($_POST['rows']) ? intval($_POST['rows']) : 10;		$subject = isset($_POST['subject']) ? mysql_real_escape_string($_POST['subject']) : '';		$assignment = isset($_POST['assignment']) ? mysql_real_escape_string($_POST['assignment']) : '';		$no_of_que = isset($_POST['no_of_que']) ? mysql_real_escape_string($_POST['no_of_que']) : '';		$submission = isset($_POST['submission']) ? mysql_real_escape_string($_POST['submission']) : '';	$type = isset($_POST['type']) ? mysql_real_escape_string($_POST['type']) : '';			$offset = ($page-1)*$rows;	$result = array();		$s5 = $_SESSION['sid'];	$dt = $_SESSION['today'];		$where = "nw_msd.id IN(			SELECT DISTINCT msd.id			FROM material_submission_details msd, material_correct_answers mca,per_material_mapping p			WHERE msd.mat_id = p.material_id AND p.student_id='$s5'			AND mca.material_id = msd.mat_id)			AND nw_msd.mat_id = m.id			AND m.material_type_id = d.id			AND m.subject_id = s.id			AND DATE(nw_msd.submission_date) > '$dt'";			$rs_q = "SELECT COUNT(nw_msd.mat_id) AS id			FROM material_submission_details nw_msd, material m, detail d, SUBJECT s			WHERE $where";	//echo $rs_q."<br/><br/><br/>";	$rs = mysql_query($rs_q);				$row = mysql_fetch_row($rs);	$result["total"] = $row[0];		$rs_qq ="SELECT nw_msd.mat_id AS id,				DATE(nw_msd.upload_date) AS comming_date,				DATE_FORMAT(DATE(nw_msd.submission_date),'%d %b %Y') AS submission,				m.material_name AS assignment, d.name AS type, s.name AS subject				FROM material_submission_details nw_msd, material m, detail d, SUBJECT s				WHERE $where";		//echo "<br/><br/>".$rs_qq."<br/><br/>";	$rs = mysql_query($rs_qq);		$items = array();	$items_count=0;	while($row = mysql_fetch_object($rs)){		array_push($items, $row);		$items_count++;	}	$result["rows"] = $items;		$result["total"] = $items_count;	echo json_encode($result);	include_once '../conn_close.php';?>